DROP SCHEMA IF EXISTS WorldStats;

CREATE SCHEMA WorldStats;
USE WorldStats;

CREATE TABLE continents_of_the_world
(
	ID INT NOT NULL AUTO_INCREMENT,
    Continent VARCHAR(33) CHARACTER SET utf8,
    Population BIGINT,
    Area_sq_mi INT,

    PRIMARY KEY (ID)
);

CREATE TABLE  countries_of_the_world 
(
	ID INT NOT NULL AUTO_INCREMENT,
    Country VARCHAR(33) CHARACTER SET utf8 NOT NULL,
    Continent INT, 
    Region VARCHAR(35) CHARACTER SET utf8,
    Population INT,
    Area_sq_mi INT,
    Pop_Density_per_sq_mi VARCHAR(12) CHARACTER SET utf8,
    Net_Migration NUMERIC(4, 2),
    Infant_mortality_per_1000_births NUMERIC(5, 2),
    GDP_per_capita INT,
    Literacy NUMERIC(4, 1),
    Phones_per_1000 NUMERIC(5, 1),
    Arable NUMERIC(4, 2),
    Crops NUMERIC(4, 2),
    Birth_Rate NUMERIC(4, 2),
    Death_Rate NUMERIC(4, 2),
    Agriculture VARCHAR(7) CHARACTER SET utf8,
    Industry NUMERIC(4, 3),
    Service NUMERIC(4, 3),
    
    PRIMARY KEY (ID)
);


